import React, { useState } from "react";

import "./FinanceForm.css";

const FinanceForm = () => {
  /*const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");*/

  const [userInput, setUserinput] = useState({
    enteredTitle: "",
    enteredAmount: "",
    enteredDate: "",
  });

  const addFinanceHandler = (event) => {
    // prevents default behavior = request not send, page not reloaded
    event.preventDefault();
    console.log("User input: " + userInput);

    const financeID = Math.floor(Math.random() * (999 - 100 + 1) + 100);
    const financeData = {
      id: financeID + "",
      title: userInput.enteredTitle,
      date: new Date(userInput.enteredDate),
      price: userInput.enteredAmount,
      currency: "CZK",
    };

    console.log("User object: " + financeData);
  };

  const titleChangeHandler = (event) => {
    // setEnteredTitle(event.target.value);

    /*setUserinput({
      ...userInput,
      enteredTitle: event.target.value,
    });*/

    setUserinput((prevState) => {
      return {
        ...prevState,
        enteredTitle: event.target.value,
      };
    });
  };

  const amountChangeHandler = (event) => {
    setUserinput((prevState) => {
      return {
        ...prevState,
        enteredAmount: event.target.value,
      };
    });
  };

  const dateChangeHandler = (event) => {
    setUserinput((prevState) => {
      return {
        ...prevState,
        enteredDate: event.target.value,
      };
    });
  };

  return (
    <form onSubmit={addFinanceHandler}>
      <div className="new-finance__controls"></div>
      <div className="new-finance__control">
        <label>Title</label>
        <input type="text" onChange={titleChangeHandler} />
      </div>
      <div className="new-finance__control">
        <label>Amount</label>
        <input type="number" min="1" step="1" onChange={amountChangeHandler} />
      </div>
      <div className="new-finance__control">
        <label>Date</label>
        <input
          type="date"
          min="2019-01-01"
          max="2022-12-31"
          onChange={dateChangeHandler}
        />
      </div>
      <div className="new-finance__actions">
        <button onClick={addFinanceHandler} type="submit">
          Add Finance
        </button>
      </div>
    </form>
  );
};

export default FinanceForm;
