import React, { useState } from "react";

import FinanceItemList from "./components/Finance/FinanceItemList";
import NewFinance from "./components/NewFinance/NewFinance";
import FinanceFilter from "./components/Finance/FinanceFilter";

const App = () => {
  const financeItems = [
    {
      id: "111",
      title: "Book",
      date: new Date(2021, 2, 8),
      price: 250,
      currency: "CZK",
    },
    {
      id: "112",
      title: "Drink",
      date: new Date(2021, 4, 8),
      price: 120,
      currency: "CZK",
    },
    {
      id: "113",
      title: "Mobile",
      date: new Date(2021, 10, 7),
      price: 19999,
      currency: "CZK",
    },
  ];

  const [financeItemsAll, setFinanceItems] = useState({
    ...financeItems,
  });

  const addExpenseDataHandler = (enteredFinanceData) => {
    financeItems.push(enteredFinanceData);
    console.log(financeItemsAll);
  };

  return (
    <div>
      <NewFinance onAddFinanceData={addExpenseDataHandler} />
      <FinanceFilter />
      <FinanceItemList financeItems={financeItems}></FinanceItemList>
    </div>
  );
};

export default App;
